select * from con_soft_task_rule
where 1=1 

		and cstr_uiid = #{uiid,jdbcType=VARCHAR}


		and ref_cstsuiid = #{cstsuiid,jdbcType=VARCHAR}


		and cstr_loccode = #{loccode,jdbcType=VARCHAR}


		and cstr_idcon1 = #{idcon1,jdbcType=VARCHAR}


		and cstr_idcon2 = #{idcon2,jdbcType=VARCHAR}


		and cstr_titlecon2 = #{titlecon2,jdbcType=VARCHAR}


		and cstr_titlecon1 = #{titlecon1,jdbcType=VARCHAR}


		and cstr_despcon2 = #{despcon2,jdbcType=VARCHAR}


		and cstr_despcon1 = #{despcon1,jdbcType=VARCHAR}


		and cstr_claname = #{claname,jdbcType=VARCHAR}


		and cstr_minlevel = #{minlevel,jdbcType=INTEGER}


		and cstr_maxlevel = #{maxlevel,jdbcType=INTEGER}


		and cstr_agloccode = #{agloccode,jdbcType=VARCHAR}


		and cstr_agidcon1 = #{agidcon1,jdbcType=VARCHAR}


		and cstr_agidcon2 = #{agidcon2,jdbcType=VARCHAR}


		and cstr_agtitle1 = #{agtitle1,jdbcType=VARCHAR}


		and cstr_agtitle2 = #{agtitle2,jdbcType=VARCHAR}


		and cstr_agdesp1 = #{agdesp1,jdbcType=VARCHAR}


		and cstr_agdesp2 = #{agdesp2,jdbcType=VARCHAR}


		and cstr_agclaname = #{agclaname,jdbcType=VARCHAR}


		and cstr_state = #{state,jdbcType=CHAR}


		and cstr_locrel = #{locrel,jdbcType=CHAR}


		and cstr_sort = #{sort,jdbcType=INTEGER}

	order by cstr_sort asc

 select con_soft_task_rule.cstr_uiid as uiid,con_soft_task_rule.ref_cstsuiid as cstsuiid, (select GROUP_CONCAT( rt1.csts_title) from con_soft_task_step rt1 where FIND_IN_SET( rt1.csts_uiid ,con_soft_task_rule.ref_cstsuiid) )  as cstsuiidtitle,con_soft_task_rule.cstr_loccode as loccode,con_soft_task_rule.cstr_idcon1 as idcon1,con_soft_task_rule.cstr_idcon2 as idcon2,con_soft_task_rule.cstr_titlecon2 as titlecon2,con_soft_task_rule.cstr_titlecon1 as titlecon1,con_soft_task_rule.cstr_despcon2 as despcon2,con_soft_task_rule.cstr_despcon1 as despcon1,con_soft_task_rule.cstr_claname as claname,con_soft_task_rule.cstr_minlevel as minlevel,con_soft_task_rule.cstr_maxlevel as maxlevel,con_soft_task_rule.cstr_agloccode as agloccode,con_soft_task_rule.cstr_agidcon1 as agidcon1,con_soft_task_rule.cstr_agidcon2 as agidcon2,con_soft_task_rule.cstr_agtitle1 as agtitle1,con_soft_task_rule.cstr_agtitle2 as agtitle2,con_soft_task_rule.cstr_agdesp1 as agdesp1,con_soft_task_rule.cstr_agdesp2 as agdesp2,con_soft_task_rule.cstr_agclaname as agclaname,con_soft_task_rule.cstr_state as state,con_soft_task_rule.cstr_locrel as locrel,con_soft_task_rule.cstr_sort as sort from con_soft_task_rule
where 1=1 

		and cstr_uiid = #{uiid,jdbcType=VARCHAR}


		and ref_cstsuiid = #{cstsuiid,jdbcType=VARCHAR}


		and cstr_loccode = #{loccode,jdbcType=VARCHAR}


		and cstr_idcon1 = #{idcon1,jdbcType=VARCHAR}


		and cstr_idcon2 = #{idcon2,jdbcType=VARCHAR}


		and cstr_titlecon2 = #{titlecon2,jdbcType=VARCHAR}


		and cstr_titlecon1 = #{titlecon1,jdbcType=VARCHAR}


		and cstr_despcon2 = #{despcon2,jdbcType=VARCHAR}


		and cstr_despcon1 = #{despcon1,jdbcType=VARCHAR}


		and cstr_claname = #{claname,jdbcType=VARCHAR}


		and cstr_minlevel = #{minlevel,jdbcType=INTEGER}


		and cstr_maxlevel = #{maxlevel,jdbcType=INTEGER}


		and cstr_agloccode = #{agloccode,jdbcType=VARCHAR}


		and cstr_agidcon1 = #{agidcon1,jdbcType=VARCHAR}


		and cstr_agidcon2 = #{agidcon2,jdbcType=VARCHAR}


		and cstr_agtitle1 = #{agtitle1,jdbcType=VARCHAR}


		and cstr_agtitle2 = #{agtitle2,jdbcType=VARCHAR}


		and cstr_agdesp1 = #{agdesp1,jdbcType=VARCHAR}


		and cstr_agdesp2 = #{agdesp2,jdbcType=VARCHAR}


		and cstr_agclaname = #{agclaname,jdbcType=VARCHAR}


		and cstr_state = #{state,jdbcType=CHAR}


		and cstr_locrel = #{locrel,jdbcType=CHAR}


		and cstr_sort = #{sort,jdbcType=INTEGER}

	order by cstr_sort asc

 select con_soft_task_rule.cstr_uiid as uiid,con_soft_task_rule.ref_cstsuiid as cstsuiid,con_soft_task_rule.ref_cstsuiid as cstsuiidrefid,con_soft_task_stepref_cstsuiid.csts_title as cstsuiidrefview,con_soft_task_rule.cstr_loccode as loccode,con_soft_task_rule.cstr_idcon1 as idcon1,con_soft_task_rule.cstr_idcon2 as idcon2,con_soft_task_rule.cstr_titlecon2 as titlecon2,con_soft_task_rule.cstr_titlecon1 as titlecon1,con_soft_task_rule.cstr_despcon2 as despcon2,con_soft_task_rule.cstr_despcon1 as despcon1,con_soft_task_rule.cstr_claname as claname,con_soft_task_rule.cstr_minlevel as minlevel,con_soft_task_rule.cstr_maxlevel as maxlevel,con_soft_task_rule.cstr_agloccode as agloccode,con_soft_task_rule.cstr_agidcon1 as agidcon1,con_soft_task_rule.cstr_agidcon2 as agidcon2,con_soft_task_rule.cstr_agtitle1 as agtitle1,con_soft_task_rule.cstr_agtitle2 as agtitle2,con_soft_task_rule.cstr_agdesp1 as agdesp1,con_soft_task_rule.cstr_agdesp2 as agdesp2,con_soft_task_rule.cstr_agclaname as agclaname,con_soft_task_rule.cstr_state as state,con_soft_task_rule.cstr_locrel as locrel,con_soft_task_rule.cstr_sort as sort from con_soft_task_rule left outer join con_soft_task_step con_soft_task_stepref_cstsuiid on con_soft_task_stepref_cstsuiid.csts_uiid = con_soft_task_rule.ref_cstsuiid
where 1=1 

		and cstr_uiid = #{uiid,jdbcType=VARCHAR}


		and ref_cstsuiid = #{cstsuiid,jdbcType=VARCHAR}


		and cstr_loccode = #{loccode,jdbcType=VARCHAR}


		and cstr_idcon1 = #{idcon1,jdbcType=VARCHAR}


		and cstr_idcon2 = #{idcon2,jdbcType=VARCHAR}


		and cstr_titlecon2 = #{titlecon2,jdbcType=VARCHAR}


		and cstr_titlecon1 = #{titlecon1,jdbcType=VARCHAR}


		and cstr_despcon2 = #{despcon2,jdbcType=VARCHAR}


		and cstr_despcon1 = #{despcon1,jdbcType=VARCHAR}


		and cstr_claname = #{claname,jdbcType=VARCHAR}


		and cstr_minlevel = #{minlevel,jdbcType=INTEGER}


		and cstr_maxlevel = #{maxlevel,jdbcType=INTEGER}


		and cstr_agloccode = #{agloccode,jdbcType=VARCHAR}


		and cstr_agidcon1 = #{agidcon1,jdbcType=VARCHAR}


		and cstr_agidcon2 = #{agidcon2,jdbcType=VARCHAR}


		and cstr_agtitle1 = #{agtitle1,jdbcType=VARCHAR}


		and cstr_agtitle2 = #{agtitle2,jdbcType=VARCHAR}


		and cstr_agdesp1 = #{agdesp1,jdbcType=VARCHAR}


		and cstr_agdesp2 = #{agdesp2,jdbcType=VARCHAR}


		and cstr_agclaname = #{agclaname,jdbcType=VARCHAR}


		and cstr_state = #{state,jdbcType=CHAR}


		and cstr_locrel = #{locrel,jdbcType=CHAR}


		and cstr_sort = #{sort,jdbcType=INTEGER}

	order by cstr_sort asc
