select * from con_soft_task_step
where 1=1 

		and csts_uiid = #{uiid,jdbcType=VARCHAR}


		and ref_cstuiid = #{cstuiid,jdbcType=VARCHAR}


		and csts_precstsuiid = #{precstsuiid,jdbcType=VARCHAR}


		and csts_title = #{title,jdbcType=VARCHAR}


		and csts_tasktype = #{tasktype,jdbcType=CHAR}


		and csts_opetype = #{opetype,jdbcType=CHAR}


		and csts_clicktype = #{clicktype,jdbcType=CHAR}


		and csts_agmodel = #{agmodel,jdbcType=CHAR}


		and csts_agcount = #{agcount,jdbcType=INTEGER}


		and csts_movesize = #{movesize,jdbcType=INTEGER}


		and csts_pagemodel = #{pagemodel,jdbcType=CHAR}


		and csts_desp = #{desp,jdbcType=VARCHAR}


		and csts_state = #{state,jdbcType=CHAR}


		and csts_submitdata = #{submitdata,jdbcType=CHAR}


		and csts_predelay = #{predelay,jdbcType=INTEGER}


