 select con_soft_task_step.csts_uiid as uiid,con_soft_task_step.ref_cstuiid as cstuiid, (select GROUP_CONCAT( rt1.cst_title) from con_soft_task rt1 where FIND_IN_SET( rt1.cst_uiid ,con_soft_task_step.ref_cstuiid) )  as cstuiidtitle,con_soft_task_step.csts_precstsuiid as precstsuiid, (select GROUP_CONCAT( rt2.csts_title) from con_soft_task_step rt2 where FIND_IN_SET( rt2.csts_uiid ,con_soft_task_step.csts_precstsuiid) )  as precstsuiidtitle,con_soft_task_step.csts_title as title,con_soft_task_step.csts_tasktype as tasktype,con_soft_task_step.csts_opetype as opetype,con_soft_task_step.csts_clicktype as clicktype,con_soft_task_step.csts_agmodel as agmodel,con_soft_task_step.csts_agcount as agcount,con_soft_task_step.csts_movesize as movesize,con_soft_task_step.csts_pagemodel as pagemodel,con_soft_task_step.csts_desp as desp,con_soft_task_step.csts_state as state,con_soft_task_step.csts_submitdata as submitdata,con_soft_task_step.csts_predelay as predelay,con_soft_task_step.csts_aftdelay as aftdelay,con_soft_task_step.csts_ingoresta as ingoresta,con_soft_task_step.csts_extracount as extracount,con_soft_task_step.csts_iffinish as iffinish,con_soft_task_step.csts_moveto as moveto,con_soft_task_step.csts_sort as sort from con_soft_task_step
where 1=1 

		and csts_uiid = #{uiid,jdbcType=VARCHAR}


		and ref_cstuiid = #{cstuiid,jdbcType=VARCHAR}


		and csts_precstsuiid = #{precstsuiid,jdbcType=VARCHAR}


		and csts_title = #{title,jdbcType=VARCHAR}


		and csts_tasktype = #{tasktype,jdbcType=CHAR}


		and csts_opetype = #{opetype,jdbcType=CHAR}


		and csts_clicktype = #{clicktype,jdbcType=CHAR}


		and csts_agmodel = #{agmodel,jdbcType=CHAR}


		and csts_agcount = #{agcount,jdbcType=INTEGER}


		and csts_movesize = #{movesize,jdbcType=INTEGER}


		and csts_pagemodel = #{pagemodel,jdbcType=CHAR}


		and csts_desp = #{desp,jdbcType=VARCHAR}


		and csts_state = #{state,jdbcType=CHAR}


		and csts_submitdata = #{submitdata,jdbcType=CHAR}


		and csts_predelay = #{predelay,jdbcType=INTEGER}


		and csts_aftdelay = #{aftdelay,jdbcType=INTEGER}


		and csts_ingoresta = #{ingoresta,jdbcType=CHAR}


		and csts_extracount = #{extracount,jdbcType=INTEGER}


		and csts_iffinish = #{iffinish,jdbcType=CHAR}


		and csts_moveto = #{moveto,jdbcType=CHAR}


		and csts_sort = #{sort,jdbcType=INTEGER}

	order by csts_sort asc

 select con_soft_task_step.csts_uiid as uiid,con_soft_task_step.ref_cstuiid as cstuiid,con_soft_task_step.ref_cstuiid as cstuiidrefid,con_soft_taskref_cstuiid.cst_title as cstuiidrefview,con_soft_task_step.csts_precstsuiid as precstsuiid,con_soft_task_step.csts_precstsuiid as precstsuiidrefid,con_soft_task_stepcsts_precstsuiid.csts_title as precstsuiidrefview,con_soft_task_step.csts_title as title,con_soft_task_step.csts_tasktype as tasktype,con_soft_task_step.csts_opetype as opetype,con_soft_task_step.csts_clicktype as clicktype,con_soft_task_step.csts_agmodel as agmodel,con_soft_task_step.csts_agcount as agcount,con_soft_task_step.csts_movesize as movesize,con_soft_task_step.csts_pagemodel as pagemodel,con_soft_task_step.csts_desp as desp,con_soft_task_step.csts_state as state,con_soft_task_step.csts_submitdata as submitdata,con_soft_task_step.csts_predelay as predelay,con_soft_task_step.csts_aftdelay as aftdelay,con_soft_task_step.csts_ingoresta as ingoresta,con_soft_task_step.csts_extracount as extracount,con_soft_task_step.csts_iffinish as iffinish,con_soft_task_step.csts_moveto as moveto,con_soft_task_step.csts_sort as sort from con_soft_task_step left outer join con_soft_task con_soft_taskref_cstuiid on con_soft_taskref_cstuiid.cst_uiid = con_soft_task_step.ref_cstuiid left outer join con_soft_task_step con_soft_task_stepcsts_precstsuiid on con_soft_task_stepcsts_precstsuiid.csts_uiid = con_soft_task_step.csts_precstsuiid
where 1=1 

		and csts_uiid = #{uiid,jdbcType=VARCHAR}


